const initState={buyCakeButton:!1,buyPizzaButton:!1,buyBurgerButton:!1,buyIceCreamButton:!1},menuReducer=(state=initState,action)=>{switch(action.type){case"CHANGE_BUTTON":{const button=action.payload;switch(button){case"cake":return{buyPizzaButton:!1,buyBurgerButton:!1,buyIceCreamButton:!1,buyCakeButton:!state.buyCakeButton};case"pizza":return{buyCakeButton:!1,buyBurgerButton:!1,buyIceCreamButton:!1,buyPizzaButton:!state.buyPizzaButton};case"burger":return{buyCakeButton:!1,buyPizzaButton:!1,buyIceCreamButton:!1,buyBurgerButton:!state.buyBurgerButton};case"icecream":return{buyPizzaButton:!1,buyBurgerButton:!1,buyCakeButton:!1,buyIceCreamButton:!state.buyIceCreamButton}}}default:return state}};export default menuReducer;